insert into launchpads (id, name, status, location, region, details) values ('vafb_slc_4e', 'Vandenberg Air Force Base Space Launch Complex 4E', 'active', 'Vandenberg Air Force Base', 'California', 'SpaceX primary west coast launch pad for polar orbits and sun synchronous orbits, primarily used for Iridium. Also intended to be capable of launching Falcon Heavy.');
insert into launchpads (id, name, status, location, region, details) values ('ccafs_slc_40', 'Cape Canaveral Air Force Station Space Launch Complex 40', 'active', 'Cape Canaveral', 'Florida', 'SpaceX primary Falcon 9 launch pad, where all east coast Falcon 9s launched prior to the AMOS-6 anomaly. Initially used to launch Titan rockets for Lockheed Martin. Back online since CRS-13 on 2017-12-15.');
insert into launchpads (id, name, status, location, region, details) values ('stls', 'SpaceX South Texas Launch Site', 'under construction', 'Boca Chica Village', 'Texas', 'SpaceX new launch site currently under construction to help keep up with the Falcon 9 and Heavy manifests. Expected to be completed in late 2018. Initially will be limited to 12 flights per year, and only GTO launches.');
insert into launchpads (id, name, status, location, region, details) values ('kwajalein_atoll', 'Kwajalein Atoll Omelek Island', 'retired', 'Omelek Island', 'Marshall Islands', 'SpaceX original launch site, where all of the Falcon 1 launches occured. Abandoned as SpaceX decided against upgrading the pad to support Falcon 9.');
insert into launchpads (id, name, status, location, region, details) values ('ksc_lc_39a', 'Kennedy Space Center Historic Launch Complex 39A', 'active', 'Cape Canaveral', 'Florida', 'NASA historic launch pad that launched most of the Saturn V and Space Shuttle missions. Initially for Falcon Heavy launches, it is now launching all of SpaceX east coast missions due to the damage from the AMOS-6 anomaly. After SLC-40 repairs are complete, it will be upgraded to support Falcon Heavy, a process which will take about two months. In the future it will launch commercial crew missions and the Interplanetary Transport System.');
insert into launchpads (id, name, status, location, region, details) values ('vafb_slc_3w', 'Vandenberg Air Force Base Space Launch Complex 3W', 'retired', 'Vandenberg Air Force Base', 'California', 'SpaceX original west coast launch pad for Falcon 1. Performed a static fire but was never used for a launch and abandoned due to scheduling conflicts.');
